INSERT INTO "Hotel" ("name",description,"location",address,rating,photos) VALUES
	 ('Hilton Hawaiian Village Waikiki Beach Resort','Located on the iconic Waikiki Beach, Hilton Hawaiian Village Waikiki Beach Resort offers luxurious accommodations and breathtaking ocean views. With multiple pools, restaurants, and activities, its the perfect destination for both relaxation and adventure.','21.2795, -157.8331','2005 Kalia Rd, Honolulu, HI 96815, United States',4.7,'{https://cdn1.goibibo.com/voy_ing/t_g/f6f87338491311e8b9fe0224510f5e5b.jpg,https://cdn1.goibibo.com/voy_ing/t_g/c828a9cc23e811ea9e340242ac110002.jpg,https://cdn1.goibibo.com/voy_ing/t_g/b6e4699e23e811ea9e340242ac110002.jpg}'),
	 ('Atlantis Paradise Island Resort','Experience the magic of Atlantis Paradise Island Resort. With its iconic architecture, stunning beaches, and world-class amenities, this resort offers an unforgettable vacation experience.','25.0865, -77.3119','One Casino Drive, Paradise Island, Bahamas',4.6,'{https://cdn1.goibibo.com/voy_ing/t_g/b660e3523ce611edbe6c0a58a9feac02.jpg,https://goreviews.goibibo.com/ugc1/t_ufs/maldives-radissonbluresortmaldives-mohammad-1633405926148.jpg,https://cdn1.goibibo.com/voy_ing/t_g/afaee5143ce511edbe6c0a58a9feac02.jpg}'),
	 ('Burj Al Arab Jumeirah','Experience unparalleled luxury at Burj Al Arab Jumeirah, the iconic sail-shaped hotel in Dubai. With its lavish suites, fine dining options, and breathtaking views, its a symbol of extravagance and indulgence.','25.1413, 55.1854','Jumeirah St - Dubai - United Arab Emirates',4.9,'{https://cdn1.goibibo.com/voy_ing/t_g/9e4f14c6416f11e9bfdf0242ac110004.jpg,https://cdn1.goibibo.com/voy_mmt_ext/t_g/htl-imgs/6608967321242204-093387d8_z.jpg,https://cdn1.goibibo.com/voy_ing/t_g/94f9c5a6416f11e99f4f0242ac110003.jpg}'),
	 ('The Ritz-Carlton, Bali','Experience luxury and serenity at The Ritz-Carlton, Bali. Nestled amidst lush greenery and overlooking the Indian Ocean, this resort offers exquisite accommodations, fine dining, and a range of recreational activities.','-8.1136, 115.1705','Jalan Raya Nusa Dua Selatan Lot III, Sawangan, Nusa Dua, Bali, 80363, Indonesia',3.9,'{https://cdn1.goibibo.com/voy_mmt_ext/t_g/htl-imgs/201905140612298119-954b6dfd_z.jpg,https://cdn1.goibibo.com/gi_dyn/t_g/https://q-xx.bstatic.com/xdata/images/hotel/max500/190196558.jpg?k=748ba3d9372222928e600beb16ae6a0ef63eb7d4677cf96ec2a719e84f9bcf39&o=&s=312x,https://cdn1.goibibo.com/gi_dyn/t_g/https://q-xx.bstatic.com/xdata/images/hotel/max500/487336937.jpg?k=a8cf19a87bcb320d2faed62a061907d9800afa06f41d3355ed187461fd2dd332&o=}'),
